mapper.select:
SELECT bc.id AS commentId, 'community' AS contentType, bc.content, u.user_id, u.nickname, bc.blind_status AS status, bc.created_at FROM board_comment bc JOIN user u ON bc.user_id = u.user_id
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND bc.blind_status = #{status}
AND bc.content LIKE CONCAT('%', #{keyword}, '%')
AND bc.created_at >= #{startDate}
AND bc.created_at <= #{endDate}
ORDER BY bc.created_at DESC

SELECT fc.id AS commentId, 'feed' AS contentType, fc.content, u.user_id, u.nickname, fc.blind_status AS status, fc.created_at FROM feed_comment fc JOIN user u ON fc.user_id = u.user_id
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND fc.blind_status = #{status}
AND fc.content LIKE CONCAT('%', #{keyword}, '%')
AND fc.created_at >= #{startDate}
AND fc.created_at <= #{endDate}
ORDER BY fc.created_at DESC

SELECT tc.id AS commentId, 'group' AS contentType, tc.content, u.user_id, u.nickname, tc.blind_status AS status, tc.created_at FROM travelmate_comment tc JOIN user u ON tc.user_id = u.user_id
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND tc.blind_status = #{status}
AND tc.content LIKE CONCAT('%', #{keyword}, '%')
AND tc.created_at >= #{startDate}
AND tc.created_at <= #{endDate}
ORDER BY tc.created_at DESC

mapper.update:
UPDATE board_comment
SET blind_status = 'BLINDED'
WHERE id = #{commentId}

UPDATE board_comment
SET blind_status = 'VISIBLE'
WHERE id = #{commentId}

UPDATE feed_comment
SET blind_status = 'BLINDED'
WHERE id = #{commentId}

UPDATE feed_comment
SET blind_status = 'VISIBLE'
WHERE id = #{commentId}

UPDATE travelmate_comment
SET blind_status = 'BLINDED'
WHERE id = #{commentId}

UPDATE travelmate_comment
SET blind_status = 'VISIBLE'
WHERE id = #{commentId}
