mapper.select:
SELECT bp.id AS postId, 'community' AS contentType, bp.title, u.user_id, u.nickname, bp.blind_status AS status, bp.created_at FROM board_post bp JOIN user u ON bp.user_id = u.user_id
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND bp.blind_status = #{status}
AND ( bp.title LIKE CONCAT('%', #{keyword}, '%') OR bp.content LIKE CONCAT('%', #{keyword}, '%') )
AND bp.created_at >= #{startDate}
AND bp.created_at <= #{endDate}
ORDER BY bp.created_at DESC

SELECT fp.id AS postId, 'feed' AS contentType, fp.title, u.user_id, u.nickname, fp.blind_status AS status, fp.created_at FROM feed_post fp JOIN user u ON fp.user_id = u.user_id
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND fp.blind_status = #{status}
AND fp.title LIKE CONCAT('%', #{keyword}, '%')
AND fp.created_at >= #{startDate}
AND fp.created_at <= #{endDate}
ORDER BY fp.created_at DESC

mapper.update:
UPDATE board_post
SET blind_status = 'BLINDED'
WHERE id = #{postId}

UPDATE board_post
SET blind_status = 'VISIBLE'
WHERE id = #{postId}

UPDATE feed_post
SET blind_status = 'BLINDED'
WHERE id = #{postId}

UPDATE feed_post
SET blind_status = 'VISIBLE'
WHERE id = #{postId}
